#!/bin/sh PATH=/var/qmail/bin:/usr/local/bin:/usr/bin:/bin export PATH case "$1" in
start) echo -n "Qmail başlatılıyor: svscan" cd /var/qmail/supervise env - PATH="$PATH" svscan & echo $! > /var/run/svscan.pid echo "." ;;
stop) echo -n "Qmail durduruluyor: svscan" kill `cat /var/run/svscan.pid` echo -n " qmail" svc -dx /var/qmail/supervise/* echo -n " logging" svc -dx /var/qmail/supervise/*/log echo "." ;;
reload|hup) echo "qmail-send'e HUP sinyali gönderiliyor." svc -h /var/qmail/supervise/qmail-send ;;
pause) echo "qmail-send bekletmeye alınıyor" svc -p /var/qmail/supervise/qmail-send echo "qmail-smtpd bekletmeye alınıyor" svc -p /var/qmail/supervise/qmail-smtpd ;;
cont) echo "qmail-send bekletmeden çıkıyor" svc -c /var/qmail/supervise/qmail-send echo "qmail-smtpd bekletmeden çıkıyor" svc -c /var/qmail/supervise/qmail-smtpd ;;
restart) echo Qmail yeniden başlatılıyor:" echo "* qmail-smtpd durduruluyor." svc -d /var/qmail/supervise/qmail-smtpd echo "* qmail-send'e SIGTERM gönderiliyor ve yeniden başlatılıyor." svc -t /var/qmail/supervise/qmail-send echo "* qmail-smtpd başlatılıyor." svc -u /var/qmail/supervise/qmail-smtpd ;;
cdb)
tcprules /etc/tcp.smtp.cdb /etc/tcp.smtp.tmp < /etc/tcp.smtp chmod 644 /etc/tcp.smtp* echo "/etc/tcp.smtp yeniden yüklendi." ;;
help)
cat <<HELP
stop -- eposta hizmeti durdurulur. (smtp bağlantıları reddedilir, dışarı birşey gönderilmez) start -- eposta hizmeti başlatılır. (smtp bağlantıları kabul edilir, postalar gönderilir) pause -- eposta hizmeti geçici olarak durdurulur. (bağlantılar kabul edilir, hiçbir şeye izin verilmez) cont -- Geçici olarak durdurulan hizmet başlatılır. cdb -- smtp için kullanılan tcpserver cdb dosyası yeniden kurgulanır. restart -- smtp durdurulur, qmail-send'e bir TERM sinyali gönderilerek qmail-send ve ardından smtp yeniden başlatılır. reload -- qmail-send HUP gönderilir, yerel ve sanal alanlar yeniden okunur. HELP ;;
*)
echo "Kullanım: $0 {start|stop|restartreload|pause|cont|cdb|help}" exit 1 ;; esac exit 0

#!/bin/sh
exec env - PATH="/var/qmail/bin:$PATH" \
qmail-start ./Maildir/ splogger qmail

