para.screen:
           #include <sys/socket.h>
#include <sys/un.h>
#include <stdlib.h>
#include <stdio.h>
#include <string.h>

#define SOKET_YOLU "/somepath"
#define LISTEN_BACKLOG 50

#define hatayı_işle(msg) \
    do { perror(msg); exit(EXIT_FAILURE); } while (0)

int
main(int argc, char *argv[])
{
    int sfd, cfd;
    struct sockaddr_un benim_addr, peer_addr;
    socklen_t peer_addr_boyut;

    sfd = socket(AF_UNIX, SOCK_STREAM, 0);
    if (sfd == -1)
        hatayı_işle("socket");

    memset(&benim_addr, 0, sizeof(benim_addr));
                        /* Yapıyı temizle */
    benim_addr.sun_family = AF_UNIX;
    strncpy(benim_addr.sun_path, SOKET_YOLU,
            sizeof(benim_addr.sun_path) - 1);

    if (bind(sfd, (struct sockaddr *) &benim_addr,
            sizeof(benim_addr)) == -1)
        hatayı_işle("bind");

    if (listen(sfd, LISTEN_BACKLOG) == -1)
        hatayı_işle("listen");

    /* Şimdi gelen bağlantıları accept(2) kullanarak
       tek tek kabul edebiliriz.*/

    peer_addr_boyut = sizeof(peer_addr);
    cfd = accept(sfd, (struct sockaddr *) &peer_addr,
                 &peer_addr_boyut);
    if (cfd == -1)
        hatayı_işle("accept");

    /* Gelen bağlantılarla ilgileneceğiniz kod... */

    /* Daha ihtiyaç yoksa, soket yol adı, SOKET_YOLU unlink(2) veya remove(3)
       kullanılarak silinmelidir. */
}
