glossdef.screen:
#define SO_EE_ORIGIN_NONE    0
#define SO_EE_ORIGIN_LOCAL   1
#define SO_EE_ORIGIN_ICMP    2
#define SO_EE_ORIGIN_ICMP6   3

struct sock_extended_err
{
    uint32_t ee_errno;   /* Hata sayısı */
    uint8_t  ee_origin;  /* Hatanın kaynağı */
    uint8_t  ee_type;    /* Tür */
    uint8_t  ee_code;    /* Kod */
    uint8_t  ee_pad;     /* Dolgu */
    uint32_t ee_info;    /* Ek bilgiler */
    uint32_t ee_data;    /* Diğer veriler */
    /* Fazladan veri takip edebilir */
};

struct sockaddr *SO_EE_OFFENDER(struct sock_extended_err *);

refsect2.screen:
struct iovec {                    /* Dağınık/toplama (scatter/gather) dizi öğeleri */
    void  *iov_base;              /* Başlangıç adresi */
    size_t iov_len;               /* İletilecek baytların saysı */
};

struct msghdr {
    void         *msg_name;       /* İsteğe bağlı adres */
    socklen_t     msg_namelen;    /* Adresin boyutu */
   struct iovec *msg_iov;        /* Dağınık/toplama (scatter/gather) dizisi */
    size_t        msg_iovlen;     /* # msg_iov içerisindeki öğeler*/
    void         *msg_control;    /* Yardımcı veri, aşağıya bakın*/
    size_t        msg_controllen; /* Yardımcı veri tampon boyutu */
    int           msg_flags;      /* Alınan iletideki bayraklar */
};

struct cmsghdr {
    size_t cmsg_len;    /* Başlıkla beraber veri bayt sayısı
                           (POSIX'de türü socklen_t'dir) */
    int    cmsg_level;  /* Kaynak protokol */
    int    cmsg_type;   /* Protokole özgü tür */
/* unsigned char cmsg_data[]; ile devam eder */
};
