refsect1.screen:
struct msghdr {
    void         *msg_name;       /* isteğe bağlı adres */
    socklen_t     msg_namelen;    /* adresin boyutu */
    struct iovec *msg_iov;        /* Scatter/gather dizisi */
    size_t        msg_iovlen;     /* # msg_iov içindeki ögeler */
    void         *msg_control;    /* yardımcı veri, aşağıya bakınız */
    size_t        msg_controllen; /* yardımcı veri arabellek boyutu */
    int           msg_flags;      /* Bayraklar (kullanılmayan) */
};
