example.eg:
/* Bilgi: HTML tarayıcı bu kuralları umursamayabilir */
body { display: inline }
p    { display: block }

p   { display: block }
em  { display: inline }
li  { display: list-item }
img { display: none }      /* resimler gösterilmez */

def.eg:
@media screen {
  h1#first { position: fixed }
}
@media print {
  h1#first { position: static }
}

example.eg:
IMG.icon {
  float: left;
  margin-left: 0;
}

div3.eg:
#harici { color: red }
#dahili { color: blue;
          width: 60px;
          border: 1px dashed gray;
          float: right; }

#harici {
    position: absolute;
    top: 100px; left: 110px;
    width: 120px;
    color: red;
    border: thin dashed gray;
}
#dahili { color: blue }

#harici {
  position: relative;
  color: red
}
#dahili {
  position: absolute;
  top: 110px; left: 10px;
  height: 60px; width: 60px;
  color: blue;
}

example.eg:
/* Bidi kuralları*/
HEBREW, HE-QUO  {direction: rtl; unicode-bidi: embed}
ENGLISH         {direction: ltr; unicode-bidi: embed}

/* Sunum kuralları */
HEBREW, ENGLISH, PAR  {display: block}
EMPH                  {font-weight: bold}
